mapper.update:
UPDATE TB_FILE_GRP
SET UPD_DT = UTC_TIMESTAMP()

    , GRP_NM = #{grpNm}


    , CATEGORY = #{category}


    , DEL_YN = #{delYn}


    , UPD_ID = #{updId}

where FILE_GRP_SEQ = #{fileGrpSeq}

UPDATE TB_FILE
SET UPD_DT = UTC_TIMESTAMP()

    , FILE_GRP_SEQ = #{fileGrpSeq}


    , ORG_IMG_NM = #{orgImgNm}


    , SYS_IMG_NM = #{sysImgNm}


    , FILE_PATH = #{filePath}


    , RMK = #{rmk}


    , DEL_YN = #{delYn}


    , UPD_ID = #{updId}

where FILE_SEQ = #{fileSeq}

mapper.select:
SELECT FILE_SEQ ,FILE_GRP_SEQ ,ORG_IMG_NM ,SYS_IMG_NM ,FILE_PATH ,RMK ,DEL_YN ,CRE_ID ,CRE_DT ,UPD_ID ,UPD_DT FROM TB_FILE WHERE DEL_YN = 'N'
AND FILE_SEQ = #{fileSeq}
AND FILE_GRP_SEQ = #{fileGrpSeq}
ORDER BY FILE_SEQ DESC
