mapper.select:
SELECT USER_SEQ
,LOGIN_TYPE
,DEVICE
,USER_ID
,EMAIL
,USER_PWD
,AUTHORITY
,VERIFY_KEY
,RESIGN_YN
,RESIGN_REASON
,RESIGN_DT
,PENDING_YN
,PENDING_DT
,PENDING_PERIOD
,DEL_YN
,CRE_ID
,CRE_DT
,UPD_ID
,UPD_DT
FROM TB_USER
WHERE DEL_YN ="N"
    AND RESIGN_YN = "N"

    AND USER_SEQ = #{userSeq}


    AND LOGIN_TYPE = #{loginType}


    AND DEVICE = #{device}


    AND USER_ID = #{userId}


    AND EMAIL = #{email}


    AND AUTHORITY = #{authority}


    AND VERIFY_KEY = #{verifyKey}


    AND RESIGN_YN = #{resignYn}


    AND RESIGN_DT = #{resignDt}


    AND PENDING_YN = #{pendingYn}


    AND PENDING_DT = #{pendingDt}


    AND PENDING_PERIOD = #{pendingPeriod}


    AND DEL_YN = #{delYn}


    AND CRE_ID = #{creId}


    AND CRE_DT = #{creDt}


    AND UPD_ID = #{updId}


    AND UPD_DT = #{updDt}
